```typescript
import * as pulumi from "@pulumi/pulumi";
import * as gcp from "@pulumi/gcp";

const development_instance = new gcp.bigtable.Instance("development-instance", {
    cluster: {
        clusterId: "tf-instance-cluster",
        storageType: "HDD",
        zone: "us-central1-b",
    },
    instanceType: "DEVELOPMENT",
});
```

