import * as random from "@pulumi/random";

const rnd = new random.RandomId("rnd", {
    byteLength: 4,
});
const example_zone = new gcp.dns.ManagedZone("example-zone", {
    description: "Example DNS zone",
    dnsName: pulumi.interpolate`example-${rnd.hex}.com.`,
