        /// Possible values are: `DELETION_RETENTION_UNSPECIFIED`, `MINIMUM`.
        /// </summary>
        [Input("retention")]
        public Input<string>? Retention { get; set; }

        /// <summary>
        /// Cloud KMS key name used for encrypting the data that is stored and replicated across runtime instances.
        /// Update is not allowed after the organization is created.
        /// If not specified, a Google-Managed encryption key will be used.
        /// Valid only when `RuntimeType` is CLOUD. For example: `projects/foo/locations/us/keyRings/bar/cryptoKeys/baz`.
        /// </summary>
        [Input("runtimeDatabaseEncryptionKeyName")]
        public Input<string>? RuntimeDatabaseEncryptionKeyName { get; set; }

        /// <summary>
        /// Runtime type of the Apigee organization based on the Apigee subscription purchased.
        /// Default value is `CLOUD`.
        /// Possible values are: `CLOUD`, `HYBRID`.
        /// </summary>
        [Input("runtimeType")]
        public Input<string>? RuntimeType { get; set; }

        /// <summary>
        /// Output only. Subscription type of the Apigee organization.
        /// Valid values include trial (free, limited, and for evaluation purposes only) or paid (full subscription has been purchased).
        /// </summary>
        [Input("subscriptionType")]
        public Input<string>? SubscriptionType { get; set; }

        public OrganizationState()
        {
        }
        public static new OrganizationState Empty => new OrganizationState();
    }
}
